def primevideo_search(driver, movie):
    driver.get("https://www.primevideo.com/")
    driver.find_element(By.ID, "pv-search-nav").send_keys(movie)
    driver.find_element(By.ID, "pv-search-nav").send_keys(Keys.ENTER)
    element = driver.find_element(By.CSS_SELECTOR, ".av-hover-wrapper:nth-child(1) img")
    actions = ActionChains(driver)
    actions.move_to_element(element).perform()
    element = driver.find_element(By.CSS_SELECTOR, "body")
    actions = ActionChains(driver)
    actions.move_to_element(element, 0, 0).perform()
    driver.find_element(By.CSS_SELECTOR, ".\\_1UBEhe .\\_1n8-io").click()
    driver.find_element(By.CSS_SELECTOR, ".fullscreenButton > .svgBackground").click()

