def youtube_search(driver, video):
    driver.get("https://www.youtube.com/")
    driver.find_element(By.NAME, "search_query").send_keys(video)
    driver.find_element(By.NAME, "search_query").send_keys(Keys.ENTER)
    element = driver.find_element(By.CSS_SELECTOR, ".ytd-item-section-renderer:nth-child(1) > #dismissable #img")
    actions = ActionChains(driver)
    actions.move_to_element(element).perform()
    element = driver.find_element(By.CSS_SELECTOR, "body")
    actions = ActionChains(driver)
    actions.move_to_element(element).perform()
    driver.find_element(By.CSS_SELECTOR, ".style-scope:nth-child(2) > #label-container").click()
